angular.module("app").directive("versionsDirective",function(){return{restrict:"EA",replace:!0,scope:{companyId:"@companyId",userId:"@userId",drawingId:"@drawingId",details:"=versionsDirective"},template:'<tr style="height: 14px;""> <td> <a href="#" ng-click="loadversion(\'{{drawinglink}}\');">{{details[1]}}</a></td> </tr>',link:function(e){e.drawinglink="/companies/"+e.companyId+"/users/"+e.userId+"/drawings/"+e.drawingId+"/changeversion?version="+e.details[0]}}});